def avg_std_deviation(numbers): 
    """
    Calculate the average and standard deviation of a list of numbers in Python.

    :param numbers: A list of numbers
    :return: (average, standard_deviation)
    """
    if not numbers:
        return (0, 0)

    avg = sum(numbers) / len(numbers)
    squared_deviations = [x - avg for x in numbers]
    deviation = math.sqrt(sum([y**2 for y in squared_deviations]) / len(squared_deviations))

    return (avg, deviation)
