 def capital_indexes(input_str):
    cap_indexes = []
    for i, char in enumerate(input_str):
        if char.isupper():
            cap_indexes.append(i)
    return cap_indexes

# Test the function with a sample string
sample_string = "AaBbCc"
print(capital_indexes(sample_string))  # Output: [0, 2]
