import re

def extract_three_char_code(filename):
    try:
        # Match three characters followed by ".usfm" in the filename
        pattern = r'(\w{3})\.usfm$'
        match = re.match(pattern, filename)
        if match:
            return match.group(1)
        else:
            raise ValueError(f"Filename {filename} does not have a three character code followed by '.usfm'")
    except Exception as e:
        print(f"An error occurred: {e}")
        return None
