 def extract_three_char_code(filename):
    try:
        import re
        if not filename.endswith(".usfm"):
            raise ValueError("Filename must have .usfm extension")

        pattern = re.compile(r"([A-Za-z0-9]{3})")
        match = pattern.search(filename)

        if not match:
            raise ValueError("No three character code found in filename")

        return match.group(1)
    except Exception as e:
        print(f"Error: {e}")
        return None

# Test the function
filename = "57-1Jn.usfm"
code = extract_three_char_code(filename)
print(f"Three character code from '{filename}': {code}")
