import re

def extract_three_char_code(filename):
    try:
        # Check if the file extension is .usfm
        if not filename.endswith('.usfm'):
            raise ValueError("File extension must be .usfm")

        # Use regex to find the three character code in the format "57-1Jn.usfm"
        match = re.search(r'[a-zA-Z0-9]{3}\.usfm', filename)

        if match:
            return match.group()
        else:
            raise ValueError("Three character code not found")

    except Exception as e:
        print(f"Error: {e}")
        return None

# Example usage
filename = "57-1Jn.usfm"
result = extract_three_char_code(filename)
print(result)  # Output: 1Jn
